CREATE OR REPLACE PROCEDURE excluir_itens_reprovados ( nr_ordem_compra_p bigint) AS $body$
BEGIN

delete	from ordem_compra_item
where 	nr_ordem_compra = nr_ordem_compra_p
and 	coalesce(dt_aprovacao::text, '') = ''
and 	(dt_reprovacao IS NOT NULL AND dt_reprovacao::text <> '');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
