SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_kit ( nr_prescricao_p bigint, cd_kit_material_p bigint, nr_seq_kit_estoque_p bigint ) AS $body$
BEGIN

if (cd_kit_material_p > 0) then
	delete	from prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	cd_kit_material	= cd_kit_material_p
	and	cd_motivo_baixa	= 0;
	commit;
elsif (nr_seq_kit_estoque_p > 0) then
	if (obter_se_exclui_kit(nr_prescricao_p, nr_seq_kit_estoque_p) = 'S') then
		update	kit_estoque
		set	nm_usuario_util	 = NULL,
					dt_utilizacao	 = NULL,
			nr_atendimento	 = NULL,
			nr_prescricao	 = NULL,
			nr_cirurgia	 = NULL
		where 	nr_sequencia	= nr_seq_kit_estoque_p;

		delete	from prescr_material
		where	nr_prescricao 	= nr_prescricao_p
		and	nr_seq_kit_estoque	= nr_seq_kit_estoque_p
		and	cd_motivo_baixa	= 0;
		commit;
	else
		CALL wheb_mensagem_pck.EXIBIR_MENSAGEM_ABORT(1015578);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
