SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_leite_sem_comp ( nr_prescricao_p bigint) AS $body$
DECLARE


nr_seq_leite_w	integer;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	prescr_leite_deriv a,
	prescr_medica c
where	a.nr_prescricao	= c.nr_prescricao
and	a.nr_prescricao = nr_prescricao_p
and	coalesce(c.dt_liberacao::text, '') = ''
and	not exists (	select	1
			from    prescr_material b
			where	a.nr_prescricao	= b.nr_prescricao
			and	b.ie_agrupador	= 16
			and	b.nr_seq_leite_deriv	= a.nr_sequencia);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_leite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM prescr_leite_deriv
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_leite_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_leite_sem_comp ( nr_prescricao_p bigint) FROM PUBLIC;

