SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_material_cartao_eme ( nr_seq_lancamento_p bigint, cd_material_p bigint, nr_seq_lote_p bigint, qt_material_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_existe_w		bigint;
qt_material_w		double precision;
cd_material_w		integer;
nr_seq_item_w		bigint;
ds_erro_w		varchar(255);


BEGIN

ds_erro_w	:= '';

select	count(*)
into STRICT	qt_existe_w
from	sup_lanc_cartao
where	nr_sequencia = nr_seq_lancamento_p
and	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');

if (qt_existe_w > 0) then
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(277807);
else
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_item_w
	from	sup_lanc_cartao_item
	where	nr_seq_lancamento = nr_seq_lancamento_p
	and	cd_material = cd_material_p
	and	coalesce(nr_seq_lote_fornec,0) = coalesce(nr_seq_lote_p,0);

	if (nr_seq_item_w = 0) then
		ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(277808);
	else
		begin

		select	cd_material,
			coalesce(max(qt_material),0)
		into STRICT	cd_material_w,
			qt_material_w
		from	sup_lanc_cartao_item
		where	nr_sequencia = nr_seq_item_w
		and	nr_seq_lancamento = nr_seq_lancamento_p
		group by cd_material;

		if (qt_material_w = qt_material_p) then
			begin

			delete
			from	sup_lanc_cartao_item
			where	nr_sequencia = nr_seq_item_w
			and	nr_seq_lancamento = nr_seq_lancamento_p;

			/*gravar_log__tasy(44001,'Excluído o material ' || cd_material_w || ' do lançamento ' ||
					nr_seq_lancamento_p || ' do cartão emergencial',nm_usuario_p);*/
			end;

		elsif (qt_material_w > qt_material_p) then
			begin

			update	sup_lanc_cartao_item
			set	qt_material = (qt_material_w - qt_material_p)
			where	nr_sequencia = nr_seq_item_w
			and	nr_seq_lancamento = nr_seq_lancamento_p;

			/*gravar_log__tasy(44001,'Diminuído a quantidade do material ' || cd_material_w || ' do lançamento ' ||
					nr_seq_lancamento_p || ' do cartão emergencial de ' ||
					qt_material_w || ' para ' || qt_material_p,nm_usuario_p);*/
			end;
		else
			ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277809);

		end if;

		end;
	end if;

	end;
end if;

if (ds_erro_w = '') then
	commit;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_material_cartao_eme ( nr_seq_lancamento_p bigint, cd_material_p bigint, nr_seq_lote_p bigint, qt_material_p bigint, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

