SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_matproc_auditoria ( nr_sequencia_p bigint, ie_tipo_p text, nr_seq_auditoria_p bigint) AS $body$
DECLARE



/* IE_TIPO_P
	'M' - Material
	'P' - Procedimento
*/
qt_registro_w		bigint;
qt_registro2_w		bigint;
nr_seq_auditoria_w	bigint;
dt_liberacao_w		timestamp;
qt_liberadas_w		bigint;
qt_total_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_auditoria
	from	auditoria_matpaci
	where	nr_seq_matpaci		=  nr_sequencia_p
	and	nr_seq_auditoria	<> nr_seq_auditoria_p
	
union all

	SELECT	a.nr_seq_auditoria
	from	auditoria_externa a,
		auditoria_externa_item b
	where	a.nr_sequencia		=  b.nr_seq_audit_ext
	and	b.ie_tipo_item		= 'M'
	and	b.nr_seq_item		=  nr_sequencia_p
	and	a.nr_seq_auditoria	<> nr_seq_auditoria_p;

C02 CURSOR FOR
	SELECT	nr_seq_auditoria
	from	auditoria_propaci
	where	nr_seq_propaci		=  nr_sequencia_p
	and	nr_seq_auditoria	<> nr_seq_auditoria_p
	
union all

	SELECT	a.nr_seq_auditoria
	from	auditoria_externa a,
		auditoria_externa_item b
	where	a.nr_sequencia		=  b.nr_seq_audit_ext
	and	b.ie_tipo_item		= 'P'
	and	b.nr_seq_item		=  nr_sequencia_p
	and	a.nr_seq_auditoria	<> nr_seq_auditoria_p;



BEGIN


if (ie_tipo_p 	= 'M') then

	select	count(*)
	into STRICT	qt_registro_w
	from	auditoria_matpaci
	where	nr_seq_matpaci		= nr_sequencia_p
	and	nr_seq_auditoria	<>nr_seq_auditoria_p;

	select	count(*)
	into STRICT	qt_registro2_w
	from	auditoria_externa a,
		auditoria_externa_item b
	where	a.nr_sequencia		=  b.nr_seq_audit_ext
	and	b.ie_tipo_item		= 'M'
	and	b.nr_seq_item		=  nr_sequencia_p
	and	a.nr_seq_auditoria	<> nr_seq_auditoria_p;

	if (qt_registro_w + qt_registro2_w	= 0) then
		update	material_atend_paciente
		set	ie_auditoria	=  'N'
		where	nr_sequencia	=  nr_sequencia_p;
	else
		qt_liberadas_w	:= 0;
		qt_total_w	:= 0;

		open C01;
		loop
		fetch C01 into
			nr_seq_auditoria_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			qt_total_w:= qt_total_w + 1;

			select 	max(dt_liberacao)
			into STRICT	dt_liberacao_w
			from 	auditoria_conta_paciente
			where 	nr_sequencia = nr_seq_auditoria_w;

			if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
				qt_liberadas_w:= qt_liberadas_w + 1;
			end if;


			end;
		end loop;
		close C01;

		if (qt_total_w = qt_liberadas_w) then
			update	material_atend_paciente
			set	ie_auditoria	=  'S'
			where	nr_sequencia	=  nr_sequencia_p
			and 	ie_auditoria	= 'A';
		end if;

	end if;
end if;

if (ie_tipo_p 	= 'P') then

	select	count(*)
	into STRICT	qt_registro_w
	from	auditoria_propaci
	where	nr_seq_propaci		= nr_sequencia_p
	and	nr_seq_auditoria	<>nr_seq_auditoria_p;

	select	count(*)
	into STRICT	qt_registro2_w
	from	auditoria_externa a,
		auditoria_externa_item b
	where	a.nr_sequencia		=  b.nr_seq_audit_ext
	and	b.ie_tipo_item		= 'P'
	and	b.nr_seq_item		=  nr_sequencia_p
	and	a.nr_seq_auditoria	<> nr_seq_auditoria_p;

	if (qt_registro_w + qt_registro2_w	= 0)  then
		update	procedimento_paciente
		set	ie_auditoria	=  'N'
		where	nr_sequencia	=  nr_sequencia_p;

	else
		qt_liberadas_w	:= 0;
		qt_total_w	:= 0;

		open C02;
		loop
		fetch C02 into
			nr_seq_auditoria_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			qt_total_w:= qt_total_w + 1;

			select 	max(dt_liberacao)
			into STRICT	dt_liberacao_w
			from 	auditoria_conta_paciente
			where 	nr_sequencia = nr_seq_auditoria_w;

			if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
				qt_liberadas_w:= qt_liberadas_w + 1;
			end if;


			end;
		end loop;
		close C02;

		if (qt_total_w = qt_liberadas_w) then
			update	procedimento_paciente
			set	ie_auditoria	=  'S'
			where	nr_sequencia	=  nr_sequencia_p
			and 	ie_auditoria	= 'A';
		end if;

	end if;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_matproc_auditoria ( nr_sequencia_p bigint, ie_tipo_p text, nr_seq_auditoria_p bigint) FROM PUBLIC;

