CREATE OR REPLACE PROCEDURE excluir_matproc_aud_item ( nr_sequencia_p bigint, ie_tipo_p text, nr_seq_auditoria_p bigint) AS $body$
DECLARE



nr_seq_item_w	auditoria_externa_item.nr_seq_item%type;

C01 CURSOR FOR
	SELECT	a.nr_seq_item
	from	auditoria_externa_item a
	where	a.nr_seq_audit_ext = nr_sequencia_p;


BEGIN
if (coalesce(nr_sequencia_p,0) <> 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL excluir_matproc_auditoria(nr_seq_item_w,ie_tipo_p,nr_seq_auditoria_p);
		end;
	end loop;
	close C01;

	delete	from	auditoria_externa
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;
end;
$body$
