CREATE OR REPLACE PROCEDURE excluir_matproc_conta_dep ( nr_seq_proc_princ_p bigint, nr_interno_conta_p bigint, cd_motivo_exc_conta_p bigint, ds_compl_motivo_excon_p text, nm_usuario_p text) AS $body$
DECLARE

 
ie_tipo_item_w	varchar(1);
nr_seq_item_w	bigint;

C01 CURSOR FOR 
	SELECT	'P' ie_tipo_item, 
		a.nr_sequencia 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta = b.nr_interno_conta 
	and	a.nr_seq_proc_princ = nr_seq_proc_princ_p 
	and	b.ie_status_acerto = 1 
	
union all
 
	SELECT	'M', 
		a.nr_sequencia 
	from	material_atend_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta = b.nr_interno_conta 
	and	a.nr_seq_proc_princ = nr_seq_proc_princ_p 
	and	b.ie_status_acerto = 1;


BEGIN 
 
if (coalesce(nr_seq_proc_princ_p,0) > 0) then 
 
	open C01;
	loop 
	fetch C01 into	 
		ie_tipo_item_w, 
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL excluir_matproc_conta(nr_seq_item_w, nr_interno_conta_p, cd_motivo_exc_conta_p, ds_compl_motivo_excon_p, ie_tipo_item_w, nm_usuario_p, null);
		end;
	end loop;
	close C01;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
