SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_matproc_dependentes (nr_seq_proc_princ_p bigint) AS $body$
DECLARE


cont_mat_w		bigint;
cont_proc_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_mat_w
from	material_atend_paciente a,
	conta_paciente b
where	a.nr_interno_conta = b.nr_interno_conta
and	b.ie_status_acerto = 2
and	a.nr_seq_proc_princ = nr_seq_proc_princ_p;


select	count(*)
into STRICT	cont_proc_w
from	procedimento_paciente a,
	conta_paciente b
where	a.nr_interno_conta = b.nr_interno_conta
and	b.ie_status_acerto = 2
and	a.nr_seq_proc_princ = nr_seq_proc_princ_p;


if (cont_proc_w = 0) and (cont_mat_w = 0) then
	delete	from material_atend_paciente
	where	nr_seq_proc_princ = nr_seq_proc_princ_p;
	delete	from procedimento_paciente
	where	nr_seq_proc_princ = nr_seq_proc_princ_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265481,'chr(13)='||chr(13));
	--Mensagem: Não é possível excluir este procedimento! || chr(13) || Existem materiais ou procedimentos dependentes em contas definitivas.
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
