SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_mat_atend_opme_lote ( cd_material_p bigint, nr_seq_mat_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_lote_fornec_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
cd_motivo_exc_conta_w		smallint;
nr_seq_map_w			bigint;
nr_interno_conta_w		bigint;
ie_status_acerto_w		smallint;
nr_atendimento_w		bigint;
dt_entrada_unidade_w		timestamp;
cd_material_w			integer;
dt_atendimento_w		timestamp;
qt_material_w			double precision;
cd_setor_atendimento_w		integer;
cd_unidade_medida_w		varchar(30);
cd_local_estoque_w		smallint;
nr_prescricao_w			bigint;
cd_cgc_w			varchar(14);
ie_estorna_mov_w		varchar(1);
qt_dev_w			integer;
					

BEGIN 
ie_estorna_mov_w := Obter_Param_Usuario(1750, 46, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_estorna_mov_w);
 
select 	max(cd_motivo_exc_conta) 
into STRICT	cd_motivo_exc_conta_w 
from	parametro_faturamento 
where	cd_estabelecimento = cd_estabelecimento_p;
 
select 	coalesce(max(x.nr_sequencia),0) 
into STRICT	nr_seq_map_w 
from	material_atend_paciente x 
where 	x.cd_material = cd_material_p 
and	coalesce(x.cd_motivo_exc_conta::text, '') = '' 
and	x.nr_seq_lote_fornec = nr_lote_fornec_p 
and	exists (SELECT 1 
		from	agenda_pac_opme a, 
			agenda_paciente b 
		where 	b.nr_sequencia = a.nr_seq_agenda 
		and	a.cd_cgc = x.cd_cgc_fornecedor 
		and	x.nr_atendimento = b.nr_atendimento 
		and	(b.ie_opme_integracao IS NOT NULL AND b.ie_opme_integracao::text <> '') 
		and  	a.ie_integracao = 'S' 
		and	a.nr_sequencia = nr_seq_mat_p) 
order by nr_sequencia;
 
if (coalesce(nr_seq_map_w,0) > 0) then 
 
	select	coalesce(max(nr_interno_conta),0) 
	into STRICT	nr_interno_conta_w 
	from	material_atend_paciente 
	where 	nr_sequencia = nr_seq_map_w;
 
	select	coalesce(max(ie_status_acerto),0) 
	into STRICT	ie_status_acerto_w 
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_w;
 
end if;
 
if (coalesce(nr_seq_map_w,0) > 0) and (coalesce(nr_lote_fornec_p,0) > 0) and (ie_status_acerto_w = 1) then 
	 
	if (ie_estorna_mov_w = 'S') then 
		 
		select	count(*) 
		into STRICT	qt_dev_w 
		from	material_atend_paciente a 
		where	nr_sequencia = nr_seq_map_w 
		and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '');
		 
		if (ie_estorna_mov_w = 'S') then 
			select	a.nr_atendimento, 
				a.dt_entrada_unidade, 
				a.dt_atendimento, 
				a.qt_material, 
				a.cd_setor_atendimento, 
				a.cd_unidade_medida, 
				a.cd_local_estoque, 
				a.nr_prescricao, 
				a.cd_cgc_fornecedor 
			into STRICT	nr_atendimento_w, 
				dt_entrada_unidade_w, 
				dt_atendimento_w, 
				qt_material_w, 
				cd_setor_atendimento_w, 
				cd_unidade_medida_w, 
				cd_local_estoque_w, 
				nr_prescricao_w, 
				cd_cgc_w 
			from	material_atend_paciente a 
			where	nr_sequencia = nr_seq_map_w 
			and	nr_seq_lote_fornec = nr_lote_fornec_p 
			and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '');
			 
			CALL gerar_prescricao_estoque(	cd_estabelecimento_p, 
							nr_atendimento_w, 
							dt_entrada_unidade_w, 
							cd_material_p, 
							dt_atendimento_w, 
							2, 
							cd_local_estoque_w, 
							qt_material_w, 
							cd_setor_atendimento_w, 
							cd_unidade_medida_w, 
							nm_usuario_p, 
							'I', 
							nr_prescricao_w, 
							0, 
							0, 
							0, 
							cd_cgc_w, 
							null, 
							nr_lote_fornec_p, 
							0, 
							null, 
							null, 
							null, 
							null, 
							null);
		end if;
	end if;
	 
	update	material_atend_paciente 
	set	nr_interno_conta  = NULL, 
		nr_seq_lote_fornec  = NULL, 
		cd_motivo_exc_conta = cd_motivo_exc_conta_w, 
		nm_usuario = nm_usuario_p 
	where 	nr_sequencia = nr_seq_map_w 
	and	nr_seq_lote_fornec = nr_lote_fornec_p;
 
elsif (ie_status_acerto_w = 2) then 
 
	ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(278795);
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_mat_atend_opme_lote ( cd_material_p bigint, nr_seq_mat_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_lote_fornec_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

