SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_mat_atend_pac_opme ( nr_seq_mat_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
cd_motivo_exc_conta_w		smallint;
ie_status_acerto_w		smallint;
nr_interno_conta_w		bigint;
nr_atendimento_w		bigint;
dt_entrada_unidade_w		timestamp;
cd_material_w			integer;
dt_atendimento_w		timestamp;
qt_material_w			double precision;
cd_setor_atendimento_w		integer;
cd_unidade_medida_w		varchar(30);
cd_local_estoque_w		smallint;
nr_prescricao_w			bigint;
cd_cgc_w			varchar(14);
ie_estorna_mov_w		varchar(1);
qt_dev_w			integer;
					

BEGIN 
ie_estorna_mov_w := Obter_Param_Usuario(1750, 46, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_estorna_mov_w);
 
 
select 	max(cd_motivo_exc_conta) 
into STRICT	cd_motivo_exc_conta_w 
from	parametro_faturamento 
where	cd_estabelecimento = cd_estabelecimento_p;
 
select	coalesce(max(nr_interno_conta),0) 
into STRICT	nr_interno_conta_w 
from	material_atend_paciente 
where 	nr_sequencia = nr_seq_mat_p;
 
select	coalesce(max(ie_status_acerto),0) 
into STRICT	ie_status_acerto_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_w;
 
if (coalesce(nr_seq_mat_p,0) > 0) and (ie_status_acerto_w = 1) then 
 
	update	material_atend_paciente 
	set	nr_interno_conta  = NULL, 
		cd_motivo_exc_conta = cd_motivo_exc_conta_w, 
		nm_usuario = nm_usuario_p 
	where 	nr_sequencia = nr_seq_mat_p;
	 
	if (ie_estorna_mov_w = 'S') then 
		 
		select	count(*) 
		into STRICT	qt_dev_w 
		from	material_atend_paciente a 
		where	nr_sequencia = nr_seq_mat_p 
		and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '');
		 
		if (coalesce(qt_dev_w,0) > 0) then 
			select	a.nr_atendimento, 
				a.dt_entrada_unidade, 
				coalesce(a.cd_material_exec, a.cd_material), 
				a.dt_atendimento, 
				a.qt_material, 
				a.cd_setor_atendimento, 
				a.cd_unidade_medida, 
				a.cd_local_estoque, 
				a.nr_prescricao, 
				a.cd_cgc_fornecedor 
			into STRICT	nr_atendimento_w, 
				dt_entrada_unidade_w, 
				cd_material_w, 
				dt_atendimento_w, 
				qt_material_w, 
				cd_setor_atendimento_w, 
				cd_unidade_medida_w, 
				cd_local_estoque_w, 
				nr_prescricao_w, 
				cd_cgc_w 
			from	material_atend_paciente a 
			where	nr_sequencia = nr_seq_mat_p 
			and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '');
			 
			CALL gerar_prescricao_estoque(	cd_estabelecimento_p, 
							nr_atendimento_w, 
							dt_entrada_unidade_w, 
							cd_material_w, 
							dt_atendimento_w, 
							2, 
							cd_local_estoque_w, 
							qt_material_w, 
							cd_setor_atendimento_w, 
							cd_unidade_medida_w, 
							nm_usuario_p, 
							'I', 
							nr_prescricao_w, 
							0, 
							0, 
							0, 
							cd_cgc_w, 
							null, 
							null, 
							0, 
							null, 
							null, 
							null, 
							null, 
							null);
		end if;
	end if;
 
elsif (ie_status_acerto_w = 2) then 
 
	ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279080);
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
