SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_mat_conta_audit (nr_sequencia_p bigint, nr_seq_matpaci_p bigint, cd_motivo_exc_conta_p bigint, ds_compl_motivo_excon_p text, nr_seq_motivo_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
ie_altera_item_repasse_w	varchar(1) := 'S';

BEGIN

ie_altera_item_repasse_w := obter_param_usuario(1116, 189, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_altera_item_repasse_w);

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	material_atend_paciente
where	nr_sequencia = nr_seq_matpaci_p;

Update	material_atend_paciente a
set 	a.cd_motivo_exc_conta = cd_motivo_exc_conta_p,
	a.ds_compl_motivo_excon = ds_compl_motivo_excon_p,
	a.nm_usuario = nm_usuario_p,
	a.dt_atualizacao = clock_timestamp(),
	a.DT_ACERTO_CONTA  = NULL,
	a.NR_INTERNO_CONTA  = NULL
where 	a.nr_sequencia = nr_seq_matpaci_p
and	not exists (	SELECT	1
			from	material_repasse x
			where	x.nr_seq_material = a.nr_sequencia
			and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

update	auditoria_matpaci a
set 	a.ie_tipo_auditoria = 'X',
	a.qt_ajuste  = NULL,
	a.nm_usuario = nm_usuario_p,
	a.dt_atualizacao = clock_timestamp(),
	a.nr_seq_motivo = CASE WHEN nr_seq_motivo_auditoria_p=0 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_auditoria_p END
where 	a.nr_sequencia = nr_sequencia_p
and	not exists (	SELECT	1
			from	material_repasse x
			where	x.nr_seq_material = a.nr_seq_matpaci
			and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
