CREATE OR REPLACE PROCEDURE excluir_mat_proc_cir ( nr_cirurgia_p bigint, cd_estabelecimento_p bigint, cd_motivo_exc_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
			 
C02 CURSOR FOR 
	SELECT	'P', 
		nr_sequencia, 
		a.nr_interno_conta 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.ie_status_acerto	= 1 
	and	nr_cirurgia		= nr_cirurgia_p 
	
union all
 
	SELECT	'M', 
		nr_sequencia, 
		a.nr_interno_conta 
	from	material_Atend_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.ie_status_acerto	= 1 
	and	nr_cirurgia		= nr_cirurgia_p;
	
	 
ie_proc_mat_w			varchar(10);
nr_sequencia_w			bigint;
nr_interno_conta_w		bigint;
				

BEGIN 
 
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (cd_motivo_exc_conta_p IS NOT NULL AND cd_motivo_exc_conta_p::text <> '') then 
	open C02;
	loop 
	fetch C02 into	 
		ie_proc_mat_w, 
		nr_sequencia_w, 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		CALL excluir_matproc_conta(nr_sequencia_w,nr_interno_conta_w,cd_motivo_exc_conta_p,null,ie_proc_mat_w,nm_usuario_p);
		end;
	end loop;
	close C02;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
