SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_mat_proc_pendente ( ie_opcao_p bigint, nr_sequencia_p text) AS $body$
BEGIN

if (ie_opcao_p = 0) then
	delete from	procedimento_repasse
	where	coalesce(nr_repasse_terceiro::text, '') = ''
	and	coalesce(nr_lote_contabil,0) = 0
	and nr_sequencia = nr_sequencia_p;
else
	delete from	material_repasse
	where	coalesce(nr_repasse_terceiro::text, '') = ''
	and	coalesce(nr_lote_contabil,0) = 0
	and nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
