CREATE OR REPLACE PROCEDURE excluir_medic_atend_ciclo_onc ( nr_seq_atendimento_p bigint, NR_SEQ_MATERIAL_p bigint, nr_agrupamento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_atendimento_w		bigint;
nr_seq_paciente_w		bigint;
nr_seq_material_w		bigint;
cd_material_w			bigint;

c01 CURSOR FOR
	SELECT	nr_seq_atendimento
	from	paciente_atendimento
	where	nr_seq_paciente	= nr_seq_paciente_w
	and	nr_seq_atendimento	<> nr_seq_atendimento_p
	and	coalesce(nr_prescricao::text, '') = '';



BEGIN

select	cd_material
into STRICT	cd_material_w
from	paciente_atend_medic
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	nr_seq_material		= NR_SEQ_MATERIAL_p;

select	nr_seq_paciente
into STRICT	nr_seq_paciente_w
from	paciente_atendimento
where	nr_seq_atendimento	= nr_seq_atendimento_p;

open C01;
loop
fetch C01 into
	nr_seq_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete  from PACIENTE_ATEND_MEDIC
	where	nr_seq_atendimento	= nr_seq_atendimento_w
	and	nr_seq_diluicao		= NR_SEQ_MATERIAL_p
	and	nr_agrupamento		= nr_agrupamento_p;

	delete	from paciente_atend_medic
	where	nr_seq_atendimento	= nr_seq_atendimento_w
	and	cd_material		= cd_material_w
	and	nr_agrupamento		= nr_agrupamento_p;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
