SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_medic_composto ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_agrupamento_p bigint) AS $body$
DECLARE


nr_sequencia_w	integer;


BEGIN

select	min(nr_sequencia)
into STRICT	nr_sequencia_w
from	prescr_material
where	nr_prescricao 	= nr_prescricao_p
and	nr_agrupamento	= nr_agrupamento_p
and	ie_agrupador		= 1;

if (nr_sequencia_w	= nr_sequencia_p) then

	delete	FROM prescr_material
	where	nr_sequencia	<> nr_sequencia_p
	and	nr_prescricao	= nr_prescricao_p
	and	nr_agrupamento	= nr_agrupamento_p
	and	ie_agrupador	= 1
	and	coalesce(nr_sequencia_solucao::text, '') = ''
	and	coalesce(nr_sequencia_proc::text, '') = ''
	and	coalesce(nr_sequencia_diluicao::text, '') = ''
	and	coalesce(nr_sequencia_dieta::text, '') = '';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
