SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_medic_kit (nr_prescricao_p prescr_medica.nr_prescricao%type, nr_sequencia_p prescr_material.nr_sequencia%type) AS $body$
DECLARE


ie_atualizado_w		boolean;


BEGIN
	ie_atualizado_w := false;

	delete	FROM prescr_material
	where	nr_sequencia <> nr_sequencia_p
	and		nr_prescricao = nr_prescricao_p
	and		nr_seq_kit = nr_sequencia_p
	--and	ie_agrupador = 1
	and		coalesce(nr_sequencia_solucao::text, '') = ''
	and		coalesce(nr_sequencia_proc::text, '') = ''
	and		coalesce(nr_sequencia_diluicao::text, '') = ''
	and		coalesce(nr_sequencia_dieta::text, '') = '';

	GET DIAGNOSTICS ie_atualizado_w = ROW_COUNT > 0;

	if (ie_atualizado_w) then
		update	prescr_medica
		set		ie_gerou_kit  = NULL,
				dt_atualizacao = clock_timestamp(),
				ds_stack = substr(ds_stack || ' atualizado ie_gerou_kit',1,1800)
		where	nr_prescricao = nr_prescricao_p;
	end if;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
