	delete 	from lib_material_paciente a
	where 	a.nr_prescricao 	= nr_prescricao_p
	and 	a.nr_seq_material 	= nr_sequencia_p
	and		coalesce(a.dt_liberacao::text, '') = '';
end if;

commit;

end;
