CREATE OR REPLACE PROCEDURE excluir_medidas_laudo ( nr_seq_medida_p bigint, nr_seq_laudo_p bigint, ie_opcao_p text) AS $body$
DECLARE


/*
M - Medida
T - Todas
*/
BEGIN

if (ie_opcao_p = 'M') then
	delete	from laudo_paciente_medida
	where	nr_seq_laudo  	= nr_seq_laudo_p
	and	nr_sequencia 	= nr_seq_medida_p;
elsif (ie_opcao_p = 'T') then
	delete	from laudo_paciente_medida
	where	nr_seq_laudo  	= nr_seq_laudo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
