CREATE OR REPLACE PROCEDURE excluir_pasta_report_dar (nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_pai_w tree_report_dar.nr_seq_pai%type;


BEGIN

select
	nr_seq_pai
into STRICT
	nr_seq_pai_w
from
	tree_report_dar
where
	nr_sequencia = nr_sequencia_p;

update
	tree_report_dar a
set
	a.nr_seq_pai = nr_seq_pai_w
where
	a.ie_tipo = 'R' and
	a.nr_sequencia in (WITH RECURSIVE cte AS (

	SELECT
		b.nr_sequencia
	from
		tree_report_dar b WHERE b.nr_sequencia = nr_sequencia_p
  UNION ALL

	SELECT
		b.nr_sequencia
	from
		tree_report_dar b JOIN cte c ON (c.prior nr_sequencia = b.nr_seq_pai)

) SELECT * FROM cte WHERE ie_tipo = 'R';
);

delete
	FROM tree_report_dar a
where
	a.ie_tipo = 'P' and
	a.nr_sequencia in (WITH RECURSIVE cte AS (

	SELECT
		b.nr_sequencia
	from
		tree_report_dar b WHERE b.nr_sequencia = nr_sequencia_p
  UNION ALL

	SELECT
		b.nr_sequencia
	from
		tree_report_dar b JOIN cte c ON (c.prior nr_sequencia = b.nr_seq_pai)

) SELECT * FROM cte WHERE ie_tipo = 'P';
);

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
