CREATE OR REPLACE PROCEDURE excluir_pendencia_vinculada ( nr_interno_conta_p bigint, nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_erro_w		varchar(60);
nr_sequencia_w		bigint;
nr_sequencia_ww		bigint;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cta_pendencia
	where	nr_seq_auditoria = nr_seq_auditoria_p
	and 	nr_interno_conta = nr_interno_conta_p
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	cta_pendencia_hist
	where	nr_seq_pend = nr_sequencia_w
	order by nr_sequencia;

C03 CURSOR FOR
	SELECT	nr_sequencia
	from	cta_pendencia_hist_envio
	where	nr_seq_pend_hist = nr_sequencia_ww
	order by nr_sequencia;



c01_w	C01%Rowtype;
c02_w	C02%Rowtype;
c03_w	C03%Rowtype;


BEGIN


open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_sequencia_w:=  c01_w.nr_sequencia;

	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		nr_sequencia_ww:=  c02_w.nr_sequencia;

		open C03;
		loop
		fetch C03 into
			c03_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin

			begin
			delete from cta_pendencia_hist_envio
			where nr_sequencia = c03_w.nr_sequencia;
			exception
				when others then
				ds_erro_w:= '';
			end;

			end;
		end loop;
		close C03;

		begin
		delete from cta_pendencia_hist
		where nr_sequencia = c02_w.nr_sequencia;
		exception
			when others then
			ds_erro_w:= '';
		end;

		end;
	end loop;
	close C02;

	begin
	delete from cta_pendencia_item
	where nr_seq_pend = c01_w.nr_sequencia;
	exception
		when others then
		ds_erro_w:= '';
	end;

	begin
	update	pep_item_pendente
	set 	nr_seq_cta_pendencia  = NULL
	where 	nr_seq_cta_pendencia = c01_w.nr_sequencia;
	exception
		when others then
		ds_erro_w:= '';
	end;

	begin
	delete	from cta_pendencia
	where 	nr_sequencia = c01_w.nr_sequencia;
	exception
		when others then
		ds_erro_w:= '';
	end;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
