SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_prescr_nao_lib ( qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE


/* o motivo de baixa deve ser o mesmo utilizado na procedure de baixa das prescrições */

nr_prescricao_w		bigint;

c010 CURSOR FOR
	SELECT	b.nr_prescricao
	from 	prescr_medica b
	where	b.dt_prescricao < (dt_parametro_p - qt_hora_adicional_p / 24)
	and	coalesce(b.dt_liberacao::text, '') = ''
	and	coalesce(b.dt_liberacao_medico::text, '') = ''
	and not exists (	SELECT	1
			from	prescr_medica x
			where	b.nr_prescricao	= x.nr_prescricao_anterior)
	and not exists (	select	1
			from	cirurgia c
			where	c.nr_prescricao = b.nr_prescricao);


BEGIN

OPEN C010;
LOOP
FETCH C010 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN

	begin
	delete from prescr_medica
	where nr_prescricao	= nr_prescricao_w;
	exception
		when others then
			nr_prescricao_w := nr_prescricao_w;
	end;
	begin
	CALL gravar_log_exclusao('PRESCR_MEDICA','JOB_EPNL','NR_PRESCRICAO : ' || nr_prescricao_w,'N');
	exception
		when others then
			nr_prescricao_w := nr_prescricao_w;
	end;
	END;
END LOOP;
close c010;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
