CREATE OR REPLACE PROCEDURE excluir_prescr_proc_exame_comp (nr_seq_peca_p bigint, nr_seq_exame_compl_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_count_peca_compl_w	bigint;

BEGIN

if (nr_seq_peca_p IS NOT NULL AND nr_seq_peca_p::text <> '') and (nr_seq_exame_compl_p IS NOT NULL AND nr_seq_exame_compl_p::text <> '')then

	delete
	from	prescr_proc_peca
	where	nr_sequencia 	= nr_seq_peca_p;

	select	count(*)
	into STRICT	nr_count_peca_compl_w
	from	prescr_proc_peca
	where	nr_seq_exame_complementar	 = nr_seq_exame_compl_p;

	if (nr_count_peca_compl_w = 0) then

		delete
		from	prescr_proc_exame_compl
		where	nr_sequencia = nr_seq_exame_compl_p;

	end if;

	commit;

end if;

end;
$body$
