CREATE OR REPLACE PROCEDURE excluir_prescr_quimioterapia ( nr_prescricao_p bigint, nr_seq_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

dt_liberacao_w	timestamp;


BEGIN

select	max(dt_liberacao)
into STRICT	dt_liberacao_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then

	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262438);

end if;

update	paciente_atendimento
set	nr_prescricao 	 = NULL
where 	nr_seq_atendimento = nr_seq_atendimento_p;

delete	FROM prescr_medica
where	nr_prescricao = nr_prescricao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
