SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_prescr_vazia_cpoe (qt_hora_adicional_p bigint) AS $body$
DECLARE

 
/* o motivo de baixa deve ser o mesmo utilizado na procedure de baixa das prescrições */
 
nr_prescricao_w		bigint;
ds_erro_w			varchar(2000);
i					bigint;

c020 CURSOR FOR 
SELECT	a.nr_prescricao 
from	prescr_medica a 
where	a.dt_prescricao < (clock_timestamp() - qt_hora_adicional_p / 24) 
and		a.dt_prescricao > clock_timestamp() - interval '5 days' 
and		coalesce(obter_itens_prescr(a.nr_prescricao, a.ds_itens_prescr)::text, '') = '' 
and not exists ( 
		SELECT	1 
		from	cirurgia b 
		where	b.nr_prescricao = a.nr_prescricao);
		

BEGIN 
i := 0;
open C020;
loop 
fetch C020 into	 
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C020 */
	begin 
	 
	begin 
	update	pe_prescricao 
	set		nr_prescricao  = NULL 
	where	nr_prescricao = nr_prescricao_w;
	commit;
	exception 
		when others then 
			ds_erro_w := substr(sqlerrm,1,200)||obter_desc_expressao(325814)||nr_prescricao_w; --' Prescricao: ' 
			RAISE NOTICE 'SAE: %', ds_erro_w;
	end;
	 
	begin 
	update	prescr_medica 
	set		dt_liberacao  = NULL, 
			dt_liberacao_medico  = NULL, 
			dt_liberacao_farmacia  = NULL 
	where	nr_prescricao = nr_prescricao_w;
	commit;
	exception 
		when others then 
			ds_erro_w := substr(sqlerrm,1,200)||obter_desc_expressao(325814)||nr_prescricao_w; --' Prescricao: ' 
			RAISE NOTICE '% %', obter_desc_expressao(724442)/*'Update: '*/
, ds_erro_w;
	end;
	begin 
	delete	from prescr_medica 
	where 	nr_prescricao	= nr_prescricao_w;
	exception 
		when others then 
			ds_erro_w := substr(sqlerrm,1,200)||obter_desc_expressao(325814)||nr_prescricao_w; --' Prescricao: ' 
			RAISE NOTICE 'Delete: %', ds_erro_w;
	end;
	 
	i := i + 1;
	if (i > 500) then 
		i := 0;
		commit;
	end if;
	 
	end;
end loop;
close C020;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
