CREATE OR REPLACE PROCEDURE excluir_proced_adic_prev ( nr_prescricao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint) AS $body$
DECLARE

			
ie_lancado_conta_w bigint := 0;
			

BEGIN

select count(1)
into STRICT ie_lancado_conta_w
from procedimento_paciente
where nr_prescricao = nr_prescricao_p
and nr_sequencia_prescricao in ( 	SELECT nr_sequencia
									FROM prescr_procedimento
									WHERE nr_prescricao            = nr_prescricao_p
									AND cd_procedimento            = cd_procedimento_p
									AND ie_origem_proced           = ie_origem_proced_p
									AND coalesce(nr_seq_proc_interno,0) = coalesce(nr_seq_proc_interno_p,0)
								);

if (ie_lancado_conta_w > 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(298604);
end if;

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then

	delete
	from    prescr_procedimento
	where   nr_prescricao 				= nr_prescricao_p
	and     cd_procedimento				= cd_procedimento_p
	and		ie_origem_proced			= ie_origem_proced_p
	and		coalesce(nr_seq_proc_interno,0)	= coalesce(nr_seq_proc_interno_p,0);
	
	commit;
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
