CREATE OR REPLACE PROCEDURE excluir_proced_atend_ciclo ( nr_seq_atendimento_p bigint, nr_seq_procedimento_p bigint, nr_agrupamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_atendimento_w		bigint;
cd_procedimento_w	bigint;
nr_seq_paciente_w	bigint;

c01 CURSOR FOR
	SELECT	nr_seq_atendimento
	from	paciente_atendimento
	where	nr_seq_paciente		= nr_seq_paciente_w
	and	nr_seq_atendimento	<> nr_seq_atendimento_p
	and	coalesce(nr_prescricao::text, '') = '';


BEGIN

select	cd_procedimento
into STRICT	cd_procedimento_w
from	paciente_atend_proc
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	nr_seq_procedimento	= nr_seq_procedimento_p;

select	nr_seq_paciente
into STRICT	nr_seq_paciente_w
from	paciente_atendimento
where	nr_seq_atendimento	= nr_seq_atendimento_p;

open C01;
loop
fetch C01 into
	nr_seq_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	from paciente_atend_proc
	where	nr_seq_atendimento	= nr_seq_atendimento_w
	and	cd_procedimento		= cd_procedimento_w
	and	nr_agrupamento		= nr_agrupamento_p;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
