SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_que_atend_questao ( nr_seq_que_atend_questao_p bigint, ie_verifica_opcao_pai_p text default 'S') AS $body$
DECLARE


nr_seq_questao_w		que_questao.nr_sequencia%type;
nr_seq_questao_opcao_w		que_questao.nr_seq_questao_opcao%type;
nr_seq_que_atendimento_w	que_atendimento.nr_sequencia%type;
nr_seq_que_questao_filha_w	que_atendimento_questao.nr_sequencia%type;
nr_seq_que_atend_questao_op_w	que_atendimento_questao_op.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	que_questao b
	where	b.nr_seq_questao_sup = nr_seq_questao_w;

BEGIN
if ( nr_seq_que_atend_questao_p > 0) then

	select	max(a.nr_seq_questao),
		max(a.nr_seq_que_atendimento),
		max(b.nr_seq_questao_opcao)
	into STRICT	nr_seq_questao_w,
		nr_seq_que_atendimento_w,
		nr_seq_questao_opcao_w
	from	que_atendimento_questao a,
		que_questao b
	where	a.nr_seq_questao = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_que_atend_questao_p;

	for r_C01 in C01
	loop

		-- verifica se existe alguma pergunta, no questionário atual, que seja filha da pergunta que está sendo excluída
		begin
			if (ie_verifica_opcao_pai_p = 'S') then
				select	nr_sequencia
				into STRICT	nr_seq_que_questao_filha_w
				from	que_atendimento_questao a
				where	a.nr_seq_questao = r_C01.nr_sequencia
				and		a.nr_seq_que_atendimento = nr_seq_que_atendimento_w
				and not exists (	SELECT	1
						from	que_atendimento_questao_op x
						where	x.nr_seq_que_atend_questao = nr_seq_que_atend_questao_p
						and		x.nr_seq_que_questao_opcao = nr_seq_questao_opcao_w )  LIMIT 1;
			else
				select	nr_sequencia
				into STRICT	nr_seq_que_questao_filha_w
				from	que_atendimento_questao a
				where	a.nr_seq_questao = r_C01.nr_sequencia
				and	a.nr_seq_que_atendimento = nr_seq_que_atendimento_w  LIMIT 1;
			end if;
		exception
			when	others then
				nr_seq_que_questao_filha_w := 0;
		end;

		if (nr_seq_que_questao_filha_w > 0) then
			if (coalesce(nr_seq_que_atend_questao_op_w::text, '') = '') then
				CALL excluir_que_atend_questao( nr_seq_que_questao_filha_w, 'N');
			end if;

		delete 	FROM que_atendimento_questao
		where	nr_sequencia = nr_seq_que_questao_filha_w;

		end if;

	end loop;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_que_atend_questao ( nr_seq_que_atend_questao_p bigint, ie_verifica_opcao_pai_p text default 'S') FROM PUBLIC;

