CREATE OR REPLACE PROCEDURE excluir_rediluicao_gpe ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_diluente_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_diluente_w
from 	prescr_material
where 	nr_prescricao = nr_prescricao_p
and 	nr_sequencia_diluicao = nr_sequencia_p
and 	ie_agrupador = 7;

CALL excluir_rediluicao(nr_prescricao_p,nr_sequencia_p,cd_perfil_p,cd_estabelecimento_p,nm_usuario_p);

if (qt_diluente_w > 0) then
	--Rediluente excluido com sucesso!
	ds_retorno_p := obter_texto_dic_objeto(110205,wheb_usuario_pck.get_nr_seq_idioma,null);
else
	--NÃ£o existe rediluente para e o medicamento!
	ds_retorno_p := obter_texto_dic_objeto(110206,wheb_usuario_pck.get_nr_seq_idioma,null);
end if;


commit;

end;
