SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_registro_pepa ( nr_seq_registro_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN
if (nr_seq_registro_p IS NOT NULL AND nr_seq_registro_p::text <> '') then

	select	count(*)
	into STRICT	qt_registros_w
	from	ehr_elemento b,
			ehr_reg_elemento a,
			ehr_reg_template c
	where	b.nr_sequencia = a.nr_seq_elemento
	and		a.nr_seq_reg_template = c.nr_sequencia
	and		c.nr_seq_reg = nr_seq_registro_p;

	if (qt_registros_w > 0) then
		--Existem registros informados para este item do PEPA#@#@
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(263600);
	end if;

	delete
	from	ehr_reg_template
	where	nr_seq_reg	=	nr_seq_registro_p;

	delete
	from	ehr_registro
	where	nr_sequencia	=	nr_seq_registro_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
