CREATE OR REPLACE PROCEDURE excluir_regra_consig_ambos ( nr_seq_regra_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;
ie_excluir_w	boolean;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	regra_forn_consig_saldo a
where	(a.nr_seq_superior IS NOT NULL AND a.nr_seq_superior::text <> '')
and	not exists (select	1
		from	regra_forn_consig_saldo b
		where	a.nr_seq_superior = b.nr_sequencia);


BEGIN
delete	FROM regra_forn_consig_saldo
where	nr_sequencia = nr_seq_regra_p;

ie_excluir_w := true;
while ie_excluir_w loop
	begin
	ie_excluir_w := false;
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		delete	FROM regra_forn_consig_saldo
		where	nr_sequencia = nr_sequencia_w;
		ie_excluir_w := true;
		end;
	end loop;
	close c01;
	end;
end loop;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
