CREATE OR REPLACE PROCEDURE excluir_sinais_vitais ( nm_tabela_p text, cd_paciente_p text, nr_atendimento_p bigint, qt_horas_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w 		atendimento_sinal_vital.nr_sequencia%type;		
			
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	atendimento_sinal_vital a,
		atendimento_paciente b
	where	a.nr_atendimento = b.nr_atendimento
	and 	(b.dt_alta IS NOT NULL AND b.dt_alta::text <> '')	
	and 	coalesce(a.ie_mediana,'0') <> '5'
	and 	a.ie_mediana_gerada = 'S'
	and	a.ie_integracao = 'S'
	and 	((a.cd_paciente = coalesce(cd_paciente_p,'0')) or (a.nr_atendimento = coalesce(nr_atendimento_p,0)))
	and 	a.dt_atualizacao <= (clock_timestamp() - (coalesce(qt_horas_p,0)/24));	

C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	atendimento_sinal_vital a,
		atendimento_paciente b
	where	a.nr_atendimento = b.nr_atendimento
	and 	(b.dt_alta IS NOT NULL AND b.dt_alta::text <> '')	
	and 	coalesce(a.ie_mediana,'0') <> '5'
	and 	a.ie_mediana_gerada = 'S'
	and	a.ie_integracao = 'S'
	and 	a.dt_atualizacao <= (clock_timestamp() - (coalesce(qt_horas_p,0)/24));	
			

BEGIN
if (nm_tabela_p = 'SV') then	
	if (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') or (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then		
		open C01;
		loop
		fetch C01 into	
			nr_sequencia_w;			
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin				
				delete from atendimento_sinal_vital where nr_sequencia = nr_sequencia_w;				
				CALL gravar_log_exclusao('ATENDIMENTO_SINAL_VITAL', nm_usuario_p, 'NR_SEQUENCIA=' || nr_sequencia_w, 'S');
			exception
			when	others then
				begin
					CALL wheb_mensagem_pck.exibir_mensagem_abort(1138675);
				end;							
			end;			
		end loop;
		close C01;
	else		
		open C02;
		loop
		fetch C02 into	
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
				delete from atendimento_sinal_vital where nr_sequencia = nr_sequencia_w;			
				CALL gravar_log_exclusao('ATENDIMENTO_SINAL_VITAL', nm_usuario_p, 'NR_SEQUENCIA=' || nr_sequencia_w, 'S');			
			exception
			when	others then
				begin
					CALL wheb_mensagem_pck.exibir_mensagem_abort(1138675);
				end;							
			end;
		end loop;
		close C02;
	end if;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
