SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_sinal_vital_nao_lib ( qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE


nr_sequencia_w	 bigint;
dt_sinal_vital_w timestamp;
cd_paciente_w	 varchar(10);
nr_atendimento_w bigint;
dt_final_w		 timestamp;
dt_inicial_w	 timestamp;

c010 CURSOR FOR
	SELECT	nr_sequencia,
		    dt_sinal_vital,
			cd_paciente,
			nr_atendimento
	  from	atendimento_sinal_vital
	 where	dt_sinal_vital between dt_inicial_w and dt_final_w
	   and	coalesce(dt_liberacao::text, '') = '';

c010_w	c010%rowtype;

BEGIN

dt_final_w		:= (dt_parametro_p - qt_hora_adicional_p / 24);
dt_inicial_w	:= dt_final_w - 30;

OPEN C010;
LOOP
FETCH C010 into
	nr_sequencia_w,
	dt_sinal_vital_w,
	cd_paciente_w,
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN
	nr_sequencia_w := nr_sequencia_w;
	begin
	delete from atendimento_sinal_vital
	where  nr_sequencia = nr_sequencia_w;
	exception
		when others then
			nr_sequencia_w := nr_sequencia_w;
	end;

	CALL gravar_log_exclusao('ATENDIMENTO_SINAL_VITAL','JOB',
			' NR_SEQUENCIA : '   || nr_sequencia_w ||
			' DT_SINAL_VITAL : ' || to_char(dt_sinal_vital_w,'dd/mm/yyyy hh24:mi:ss') ||
			' CD_PACIENTE : '    || cd_paciente_w ||
			' NR_ATENDIMENTO : ' || nr_atendimento_w
			,'N');

	END;
END LOOP;
close c010;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
