CREATE OR REPLACE PROCEDURE excluir_solic_compra ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_motivo_baixa_w	integer;
cd_estabelecimento_w	smallint;

 
/* excluir solic_compra */
 
 

BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	solic_compra 
where	nr_solic_compra = nr_solic_compra_p;
 
select	coalesce((substr(Obter_Valor_Param_Usuario(913,26,null,nm_usuario_p,cd_estabelecimento_w),1,5))::numeric ,2) 
into STRICT	cd_motivo_baixa_w
;
 
update	solic_compra 
set	dt_baixa		= clock_timestamp(), 
	cd_motivo_baixa	= cd_motivo_baixa_w, 
	nm_usuario		= nm_usuario_p 
where	nr_solic_compra   	= nr_solic_compra_p;
 
update	solic_compra_item 
set	dt_baixa		= clock_timestamp(), 
	cd_motivo_baixa	= cd_motivo_baixa_w, 
	nm_usuario		= nm_usuario_p 
where	nr_solic_compra   	= nr_solic_compra_p 
and	coalesce(nr_cot_compra::text, '') = '';
 
CALL gerar_hist_solic_sem_commit( 
	nr_solic_compra_p, 
	wheb_mensagem_pck.get_texto(300784), 
	wheb_mensagem_pck.get_texto(300785), 
	'B', 
	'TASY');
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
