CREATE OR REPLACE PROCEDURE excluir_sol_sem_comp ( nr_prescricao_p bigint) AS $body$
DECLARE


nr_seq_solucao_w	integer;

c01 CURSOR FOR
SELECT	a.nr_seq_solucao
from	prescr_solucao a
where	a.nr_prescricao	= nr_prescricao_p
and	a.ie_hemodialise not in ('S','P')
and	not exists (	select	1
			from	prescr_material b
			where	a.nr_prescricao	= b.nr_prescricao
			and	b.ie_agrupador	= 4
			and	b.nr_sequencia_solucao	= a.nr_seq_solucao);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_solucao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_sol_sem_comp ( nr_prescricao_p bigint) FROM PUBLIC;

