SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_titulo_pagar_desdob (nr_titulo_p bigint) AS $body$
DECLARE


cont_w		integer;


BEGIN

select	count(*)
into STRICT	cont_w
from	titulo_pagar
where	nr_titulo_original	= nr_titulo_p
and	ie_situacao	<> 'A';

if (cont_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265486,'');
	--Alguns títulos deste desdobramento não estão abertos!
else
	update repasse_terceiro_venc
	set	nr_titulo	 = NULL
	where	nr_titulo	= nr_titulo_p;

	delete from titulo_pagar_desdob
	where	nr_titulo	= nr_titulo_p;

	update	nota_fiscal_venc
	set	nr_titulo_pagar	 = NULL
	where	nr_titulo_pagar	= nr_titulo_p;

	update	nota_fiscal_venc a
	set	a.nr_titulo_pagar = NULL
	where	exists (SELECT	1
			from 	titulo_pagar x
			where	x.nr_titulo		= a.nr_titulo_pagar
			and	x.nr_titulo_original	= nr_titulo_p);

	delete	from titulo_pagar
	where	nr_titulo_original = nr_titulo_p;

	delete	from titulo_pagar
	where	nr_titulo	= nr_titulo_p;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_titulo_pagar_desdob (nr_titulo_p bigint) FROM PUBLIC;

