SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_trans_tef ( nr_seq_movto_p bigint, vl_transacao_p bigint, nr_seq_caixa_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_movto_p IS NOT NULL AND nr_seq_movto_p::text <> '') then

	delete
	from	movto_cartao_cr_parcela
	where	nr_seq_movto = nr_seq_movto_p;

	delete
	from	movto_cartao_cr
	where	nr_sequencia = nr_seq_movto_p;

	CALL gravar_log_tasy(
		88939,
		substr(wheb_mensagem_pck.get_texto(324412,'NR_SEQ_MOVTO_P=' || nr_seq_movto_p ||
							';VL_TRANSACAO_P=' || vl_transacao_p ||
							';NR_SEQ_CAIXA_P=' || nr_seq_caixa_p),1,2000),
		nm_usuario_p);

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
