SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE exclui_kit_agrupado (nr_prescricao_p bigint, nr_seq_reg_kit_p bigint, nm_usuario_p text default null) AS $body$
DECLARE


nr_seq_kit_w	bigint:=0;
nm_usuario_w	varchar(15);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	kit_estoque
	where	nr_seq_reg_kit = nr_seq_reg_kit_p;


BEGIN

nm_usuario_w	 := coalesce(nm_usuario_p,wheb_usuario_pck.get_nm_usuario);

open C01;
loop
fetch C01 into
	nr_seq_kit_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL excluir_kit(nr_prescricao_p,0, nr_seq_kit_w);
	end;
end loop;
close C01;

if (coalesce(nr_seq_reg_kit_p,0) > 0) then
	update	kit_estoque_reg
	set	dt_utilizacao 	 = NULL,
		nm_usuario_util  = NULL,
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_w
	where	nr_sequencia 	= nr_seq_reg_kit_p;
end if;

/*Rotina para excluir os materiais avulsos do registro de kit*/

if (nr_seq_reg_kit_p > 0) then
	delete	from prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_reg_kit	= nr_seq_reg_kit_p
	and	coalesce(nr_seq_kit_estoque::text, '') = ''
	and	cd_motivo_baixa	= 0;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
