SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE exclui_prescr_procedimento ( nr_prescr_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_prescr_p <> 0) then
	delete   FROM prescr_procedimento a
	where    a.nr_prescricao         = nr_prescr_p   
	and      (a.nr_seq_proc_princ IS NOT NULL AND a.nr_seq_proc_princ::text <> '')    
	and      not exists (	SELECT 1 
							from prescr_procedimento b 
							where a.nr_prescricao = b.nr_prescricao 
							and b.nr_seq_interno = a.nr_seq_proc_princ);
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
