SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE exclui_rack (nr_seq_armazem_rack_p bigint, rack_excluido_p INOUT text) AS $body$
DECLARE


excluido_w          varchar(1);
nr_sequencia_info_w bigint;
nr_total_info_w     bigint;
nr_seq_amostra_w    LAB_SORO_AMOSTRA_RACK.nr_sequencia%type;

c01 CURSOR FOR
  SELECT nr_sequencia
    from LAB_SORO_AMOSTRA_RACK
   where nr_seq_armazem_rack = nr_seq_armazem_rack_p and
         ie_status <> 'L';

BEGIN
  excluido_w := 'N';
  begin
    delete from lab_soro_processo_info
     where nr_seq_armazena_rack = nr_seq_armazem_rack_p;

    open C01;
    loop
      fetch c01 into
        nr_seq_amostra_w;
    EXIT WHEN NOT FOUND; /* apply on c01 */
      delete from lab_soro_exame_amostra
       where nr_seq_amostra_rack = nr_seq_amostra_w;

      commit;
    end loop;
    close c01;

    delete from LAB_SORO_AMOSTRA_RACK
    where nr_seq_armazem_rack = nr_seq_armazem_rack_p;

    delete from lab_soro_armazena_rack
     where nr_sequencia = nr_seq_armazem_rack_p;

    excluido_w := 'S';
    commit;
	exception
  when others then
    excluido_w := 'N';
  end;

  rack_excluido_p := excluido_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
