SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excuir_mat_cirur_conta (nr_prescricao_p bigint, nr_sequencia_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


qt_mat_atend_agenda_w		integer;


BEGIN

select 	count(*)
into STRICT	qt_mat_atend_agenda_w
from   	material_atend_paciente
where  	nr_prescricao 		   	= nr_prescricao_p
and	   	nr_sequencia_prescricao = nr_sequencia_p
and	   	coalesce(cd_motivo_exc_conta::text, '') = '';

if (qt_mat_atend_agenda_w > 0) then
		ds_erro_p := wheb_mensagem_pck.get_texto(289742);
else
	update 	material_atend_paciente
	set	   	nr_prescricao 			 = NULL,
			nr_sequencia_prescricao	 = NULL
	where  	nr_prescricao 		    = nr_prescricao_p
	and	   	nr_sequencia_prescricao = nr_sequencia_p
	and	   	(cd_motivo_exc_conta IS NOT NULL AND cd_motivo_exc_conta::text <> '');

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
