CREATE OR REPLACE PROCEDURE executar_evento_agenda_quimio (nr_seq_paciente_p text, dt_prevista_p timestamp, nm_usuario_p text) AS $body$
DECLARE


ie_status_w		varchar(3);
cd_pessoa_fisica_w	varchar(10);

C01 CURSOR FOR
	SELECT	a.ie_status
	from	status_evento_agenda a	
	where (a.cd_estabelecimento = obter_estabelecimento_ativo or exists (	SELECT 1 
												from STATUS_EVENTO_AGENDA_ESTAB x 
												where x.cd_estabelecimento = obter_estabelecimento_ativo
												and a.nr_sequencia 	   = x.nr_seq_status_evento_ag ))	
	and	a.ie_evento = 'TP'
	and	coalesce(a.cd_perfil,obter_perfil_ativo)	= obter_perfil_ativo
	order by a.ie_status;
	

BEGIN

open C01;
loop
fetch C01 into	
	ie_status_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	paciente_setor
	where	nr_seq_paciente	= nr_seq_paciente_p;
	
	update	agenda_paciente
	set 	ie_status_agenda = ie_status_w,
          	dt_executada 	 = CASE WHEN ie_status_w='E' THEN  clock_timestamp()  ELSE dt_executada END
	where	trunc(dt_agenda) = trunc(dt_prevista_p)
	and 	cd_pessoa_fisica = cd_pessoa_fisica_w;
	
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
