CREATE OR REPLACE PROCEDURE exec_proc_baca ( nm_objeto_baca_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		integer;
ds_comando_w		varchar(4000);
nm_objeto_baca_w	varchar(128);


BEGIN

nm_objeto_baca_w := Upper(nm_objeto_baca_p);

--Cria BACA
ds_comando_w	:= 'BEGIN TASY_CRIAR_OBJETO_SISTEMA('|| chr(39) || nm_objeto_baca_w || chr(39) ||','||chr(39) ||'TASY_VERSAO'|| chr(39)||'); END;';
CALL exec_sql_dinamico(nm_usuario_p,ds_comando_w);

--Verifica se a BACA foi criada corretamente
select	count(*)
into STRICT	qt_registros_w
from	user_objects
where	object_name = nm_objeto_baca_w;

if (qt_registros_w = 0) then
	begin
	CALL GRAVAR_LOG_ATUALIZACAO_ERRO(190,nm_objeto_baca_w,obter_desc_expressao(786740)/*'Nao foi possÃ­vel criar a BACA'*/
,nm_usuario_p);
	end;
end if;

--Executa BACA
CALL exec_sql_dinamico(nm_usuario_p,'begin ' || nm_objeto_baca_w || '; end;');

--Elimina BACA
select	count(*)
into STRICT	qt_registros_w
from	user_objects
where	object_name = nm_objeto_baca_w;

if (qt_registros_w > 0) then
	begin
	CALL exec_sql_dinamico(nm_usuario_p, 'drop procedure ' || nm_objeto_baca_w);
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
