SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE exec_regra_fim_evento ( nr_cirurgia_p bigint, nr_seq_evento_dif_p bigint, nm_usuario_p text) AS $body$
DECLARE

							 
qt_procedimento_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
dt_registro_w			timestamp;
dt_inicio_real_w		timestamp;
nr_atendimento_w		bigint;
qt_regra_w			bigint;
nr_seq_proc_interno_w		bigint;
nr_seq_evento_w			bigint;
cd_tipo_anestesia_w		varchar(10);

c01 CURSOR FOR							 
	SELECT	nr_seq_proc_interno, 
		nr_seq_evento 
	from	regra_lanc_tempo_evento 
	where	nr_seq_evento_dif 				= nr_seq_evento_dif_p 
	and	coalesce(cd_tipo_anestesia,cd_tipo_anestesia_w)	= cd_tipo_anestesia_w;

 

BEGIN 
select	count(*) 
into STRICT	qt_regra_w 
from	regra_lanc_tempo_evento 
where	nr_seq_evento_dif = nr_seq_evento_dif_p;
 
if (qt_regra_w > 0) and (coalesce(nr_cirurgia_p,0) > 0) then 
	 
	select	max(c.nr_atendimento), 
		coalesce(max(c.cd_tipo_anestesia),'XPTO') 
	into STRICT	nr_atendimento_w, 
		cd_tipo_anestesia_w 
	from	cirurgia c 
	where	c.nr_cirurgia = nr_cirurgia_p;
	 
	/* 
	select	max(nr_seq_proc_interno), 
		max(nr_seq_evento) 
	into	nr_seq_proc_interno_w, 
		nr_seq_evento_w 
	from	regra_lanc_tempo_evento 
	where	nr_seq_evento_dif 				= nr_seq_evento_dif_p 
	and	nvl(cd_tipo_anestesia,cd_tipo_anestesia_w)	= cd_tipo_anestesia_w; 
	*/
 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_proc_interno_w, 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then 
			SELECT * FROM Obter_Proc_Tab_Interno(	nr_seq_proc_interno_w, null, nr_atendimento_w, null, cd_procedimento_w, ie_origem_proced_w, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
			 
									 
			select	max(dt_registro) 
			into STRICT	dt_inicio_real_w 
			from	evento_cirurgia_paciente 
			where	nr_seq_evento	= nr_seq_evento_w 
			and	nr_cirurgia	= nr_cirurgia_p 
			and	coalesce(ie_situacao,'A') = 'A';
			 
			 
			select	max(dt_registro) 
			into STRICT	dt_registro_w 
			from	evento_cirurgia_paciente 
			where	nr_seq_evento	= nr_seq_evento_dif_p 
			and	nr_cirurgia	= nr_cirurgia_p 
			and	coalesce(ie_situacao,'A') = 'A';
 
			select	max(coalesce(obter_qte_proced_cirurgia(cd_procedimento_w, ie_origem_proced_w, dt_registro_w, dt_inicio_real_w),0)) 
			into STRICT	qt_procedimento_w 
			;
 
			CALL gerar_lancto_automatico_conta(	nr_atendimento_w, 
							nr_cirurgia_p, 
							null, 
							null, 
							null, 
							nr_seq_proc_interno_w, 
							cd_procedimento_w, 
							ie_origem_proced_w, 
							qt_procedimento_w, 
							nm_usuario_p, 
							null, 
							null, 
							null);
											 
			commit;
		end if;	
		end;
	end loop;
	close C01;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
