CREATE OR REPLACE PROCEDURE exibir_imagem_laudo_paciente ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, cd_estabelecimento_p bigint, nr_seq_interno_p INOUT bigint, nr_acesso_dicom_p INOUT text, cd_procedimento_p INOUT bigint, ie_origem_proced_p INOUT bigint, cd_tipo_procedimento_p INOUT bigint, ds_url_pacs_edicao_p INOUT text, ds_url_pacs_p INOUT text, ds_diretorio_pacs_p INOUT text ) AS $body$
DECLARE


nr_seq_interno_w		bigint;
nr_acesso_dicom_w	varchar(20);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_tipo_procedimento_w	smallint;
ds_url_pacs_edicao_w	varchar(255);
ds_url_pacs_w		varchar(255);
ds_diretorio_pacs_w	varchar(255);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '')then
	begin
	select	distinct
		max(b.nr_seq_interno),
		max(substr(Obter_nr_acesso_dicom(a.nr_prescricao, b.nr_sequencia, c.nr_sequencia),1,20)) nr_acesso_dicom,
		max(b.cd_procedimento),
		max(b.ie_origem_proced)
	into STRICT	nr_seq_interno_w,
		nr_acesso_dicom_w,
		cd_procedimento_w,
		ie_origem_proced_w
	from	procedimento_paciente c,
		prescr_procedimento b,
		prescr_medica a
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_prescricao = nr_prescricao_p
	and	b.nr_prescricao = c.nr_prescricao
	and	b.nr_sequencia = c.nr_sequencia_prescricao
	and	b.nr_sequencia = nr_seq_prescricao_p;


	select	max(cd_tipo_procedimento)
	into STRICT	cd_tipo_procedimento_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;

	select	max(ds_url_pacs_edicao) ,
		max(ds_url_pacs),
		max(ds_diretorio_pacs)
	into STRICT 	ds_url_pacs_edicao_w,
		ds_url_pacs_w,
		ds_diretorio_pacs_w
	from	parametro_medico
	where 	cd_estabelecimento = cd_estabelecimento_p;
	end;
end if;


nr_seq_interno_p		:= nr_seq_interno_w;
nr_acesso_dicom_p		:= nr_acesso_dicom_w;
cd_procedimento_p		:= cd_procedimento_w;
ie_origem_proced_p	:= ie_origem_proced_w;
cd_tipo_procedimento_p	:= cd_tipo_procedimento_w;
ds_url_pacs_edicao_p	:= ds_url_pacs_edicao_w;
ds_url_pacs_p		:= ds_url_pacs_w;
ds_diretorio_pacs_p		:= ds_diretorio_pacs_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
