CREATE OR REPLACE PROCEDURE exibir_ocultar_grafico_apap ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	update	w_pep_apap
	set	ie_grafico	= CASE WHEN coalesce(ie_grafico,'N')='S' THEN  null  ELSE 'S' END ,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
