CREATE OR REPLACE PROCEDURE exibir_w_ficha_financ_coluna ( nr_seq_linha_p bigint, ie_exibir_p text, nm_usuario_p text, ie_atualizou_p INOUT text) AS $body$
DECLARE


nr_seq_linha_w	bigint;
ie_exibir_w	varchar(1);
ie_atualizou_w	varchar(1);
ie_atualizou_ww	varchar(1);

c01 CURSOR FOR
SELECT	nr_sequencia
from	w_ficha_financ_coluna
where	nm_usuario = nm_usuario_p
and	nr_seq_superior = nr_seq_linha_p;


BEGIN
ie_exibir_w	:= ie_exibir_p;

if (coalesce(ie_exibir_p::text, '') = '') then
	begin
	select	CASE WHEN coalesce(max(ie_exibir),'N')='S' THEN  'N'  ELSE 'S' END
	into STRICT	ie_exibir_w
	from	w_ficha_financ_coluna
	where	nm_usuario = nm_usuario_p
	and	nr_seq_superior = nr_seq_linha_p;

	update	w_ficha_financ_coluna
	set	ie_exibir = ie_exibir_w
	where	nm_usuario = nm_usuario_p
	and	nr_seq_superior = nr_seq_linha_p;

	if (NOT FOUND) then
		ie_atualizou_w	:= 'N';
	else
		ie_atualizou_w	:= 'S';
	end if;
	end;
end if;

if (ie_exibir_w = 'N') then
	open c01;
	loop
	fetch c01 into
		nr_seq_linha_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		update	w_ficha_financ_coluna
		set	ie_exibir = ie_exibir_w
		where	nm_usuario = nm_usuario_p
		and	nr_seq_superior = nr_seq_linha_w;

		ie_atualizou_ww := exibir_w_ficha_financ_coluna(	nr_seq_linha_w, ie_exibir_w, nm_usuario_p, ie_atualizou_ww);
		end;
	end loop;
	close c01;
end if;

commit;

ie_atualizou_p	:= ie_atualizou_w;

end;
$body$
