SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE exportar_anexo_agenda_ged ( nm_usuario_p text, cd_profissional_p text, nr_seq_agenda_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
ds_arquivo_w		varchar(255);
ds_titulo_w			varchar(255);
cd_estabelecimento_w	ged_atendimento.cd_estabelecimento%type;

c01 CURSOR FOR
SELECT	ds_arquivo
from	anexo_agenda
where	nr_seq_agenda	= nr_seq_agenda_p;				


BEGIN

select 	max(nr_atendimento),
	max(cd_pessoa_fisica)
into STRICT	nr_atendimento_w,
	cd_pessoa_fisica_w
from	agenda_paciente
where	nr_sequencia 	=	nr_seq_agenda_p;

ds_titulo_w	:= wheb_mensagem_pck.get_texto(300734);
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
open C01;
loop
fetch C01 into	
	ds_arquivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select 	nextval('ged_atendimento_seq')
	into STRICT	nr_sequencia_w
	;

	insert into ged_atendimento(
			nr_sequencia,
			ds_arquivo,
			ds_titulo,
			cd_profissional,
			dt_registro,
			dt_atualizacao,
			nm_usuario,
			cd_pessoa_fisica,
			nr_atendimento,
			cd_estabelecimento)
	values (	nr_sequencia_w,
			ds_arquivo_w,
			ds_titulo_w,
			cd_profissional_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			nr_atendimento_w,
			cd_estabelecimento_w);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
