CREATE OR REPLACE PROCEDURE exportar_aval_same ( nr_atendimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_procedimento_sus_w	bigint;
ie_origem_proced_sus_w	bigint;
cd_cid_primario_w	varchar(10);
cd_cid_secundario_w	varchar(10);
qt_existente_w		bigint;

BEGIN

select	max(cd_procedimento),
	max(cd_cid_primario),
	max(ie_origem_proced)
into STRICT	cd_procedimento_w,
	cd_cid_primario_w,
	ie_origem_proced_w
from	avaliacao_paciente_cih
where	nr_atendimento = nr_atendimento_p;

if (cd_procedimento_w <> 0) then

	select  coalesce(max(cd_procedimento_sus),0),
		coalesce(max(ie_origem_proced_sus),0)
	into STRICT	cd_procedimento_sus_w,
		ie_origem_proced_sus_w
	from	procedimento_conv_sus
	where	cd_procedimento  = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;

	If (ie_origem_proced_w in (2,3,7)) then
		cd_procedimento_sus_w  := cd_procedimento_w;
		ie_origem_proced_sus_w := ie_origem_proced_w;
	End if;

	if (cd_procedimento_sus_w = 0) then
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279609,null);
	else
		select 	max(cd_doenca)
		into STRICT	cd_cid_secundario_w
		from	diagnostico_doenca
		where 	ie_classificacao_doenca = 'S'
		and	nr_atendimento = nr_atendimento_p;

		select	count(*)
		into STRICT	qt_existente_w
		from	procedimento_paciente_cih
		where	nr_atendimento = nr_atendimento_p;

		If (qt_existente_w > 0) then
			update	procedimento_paciente_cih
			set	cd_procedimento = cd_procedimento_sus_w,
				ie_origem_proced = ie_origem_proced_sus_w,
				cd_cid_primario = cd_cid_primario_w,
				cd_cid_secundario = cd_cid_secundario_w
			where	nr_atendimento = nr_atendimento_p;
		Else
			insert into procedimento_paciente_cih(	nr_atendimento,
							nr_sequencia,
							cd_procedimento,
							ie_origem_proced,
							dt_atualizacao,
							nm_usuario,
							cd_cid_primario,
							cd_cid_secundario)
			values (	nr_atendimento_p,
							1,
							cd_procedimento_sus_w,
							ie_origem_proced_sus_W,
							clock_timestamp(),
							nm_usuario_p,
							cd_cid_primario_w,
							cd_cid_secundario_w);
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
