CREATE OR REPLACE PROCEDURE fanep_hemo_bfpost_js ( nr_seq_pepo_p bigint, ie_derivado_exame_p bigint, nr_sequencia_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p INOUT bigint, ie_origem_proced_p INOUT bigint) AS $body$
DECLARE

 
cd_categoria_w		varchar(10);
cd_convenio_w		integer;
ie_tipo_atendimento_w	integer;
ie_tipo_convenio_w	smallint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_proc_interno_w	bigint;
cd_setor_w		integer;
				

BEGIN 
 
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then 
		begin 
 
				select 	Obter_Categoria_Atendimento(nr_atendimento) 
				into STRICT	cd_categoria_w 
				from 	pepo_cirurgia 
				where 	nr_sequencia = nr_seq_pepo_p;
				 
				select 	obter_convenio_atendimento(nr_atendimento) 
				into STRICT	cd_convenio_w 
				from 	pepo_cirurgia 
				where 	nr_sequencia = nr_seq_pepo_p;
				 
				select 	obter_tipo_atendimento(nr_atendimento) 
				into STRICT 	ie_tipo_atendimento_w 
				from 	pepo_cirurgia 
				where 	nr_sequencia = nr_seq_pepo_p;
				 
				ie_tipo_convenio_w	:=	obter_tipo_convenio(cd_convenio_w);
		 
				SELECT * FROM Obter_Proced_sangue(	ie_derivado_exame_p, nr_sequencia_p, cd_estabelecimento_p, ie_tipo_atendimento_w, ie_tipo_convenio_w, cd_convenio_w, cd_categoria_w, cd_setor_w, cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w) INTO STRICT cd_setor_w, cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w;
					 
				cd_procedimento_p	:=	cd_procedimento_w;
				ie_origem_proced_p	:= 	ie_origem_proced_w;
				 
		end;
	end if;
 
commit;
 
end;
$body$
