CREATE OR REPLACE PROCEDURE far_altera_qtde_item_ped ( nr_seq_pedido_p bigint, nr_seq_item_p bigint, qt_item_p bigint) AS $body$
DECLARE


vl_unitario_item_w			double precision;
vl_total_item_w			double precision;
vl_desconto_w			double precision;
vl_liquido_item_w			double precision;
vl_rateado_w			double precision;


BEGIN

select	vl_unitario_item,
	vl_desconto,
	vl_rateado
into STRICT	vl_unitario_item_w,
	vl_desconto_w,
	vl_rateado_w
from	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p
and	nr_seq_item = nr_seq_item_p;

vl_total_item_w	:= round((qt_item_p * vl_unitario_item_w)::numeric,2);
vl_liquido_item_w	:= (coalesce(vl_total_item_w,0) - (coalesce(vl_desconto_w,0) + coalesce(vl_rateado_w,0)));

update	far_pedido_item
set	qt_material = qt_item_p,
	vl_total_item = vl_total_item_w,
	vl_liquido_item = vl_liquido_item_w,
	vl_desconto = (vl_desconto_w * qt_item_p)
where	nr_seq_pedido = nr_seq_pedido_p
and	nr_seq_item = nr_seq_item_p;

commit;

CALL far_atualizar_vl_total_ped(nr_seq_pedido_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
